mapper.insert:
insert into COM_OPERATION_LOG (PK_ID, ACTION_TYPE, ACTION_URL,
  ACTION_USER, ACTION_TIME, ACTION_CONTENT
  )
values (#{pkId,jdbcType=VARCHAR}, #{actionType,jdbcType=CHAR}, #{actionUrl,jdbcType=VARCHAR},
  #{actionUser,jdbcType=VARCHAR}, #{actionTime,jdbcType=VARCHAR}, #{actionContent,jdbcType=VARCHAR}
  )

mapper.select:
select PK_ID, ACTION_TYPE, ACTION_URL, ACTION_USER, ACTION_TIME, ACTION_CONTENT
from COM_OPERATION_LOG
where PK_ID = #{pkId,jdbcType=VARCHAR}

select PK_ID, ACTION_TYPE, ACTION_URL, ACTION_USER, ACTION_TIME, ACTION_CONTENT
from COM_OPERATION_LOG

  and PK_ID = #{pkId}
  and ACTION_TYPE = #{actionType}
  and ACTION_URL like '%' || #{actionUrl} || '%'
  and ACTION_USER like '%' || #{actionUser} || '%'
  and ACTION_TIME >= #{actionTimeStart}
  and #{actionTimeEnd} >= ACTION_TIME
  and ACTION_CONTENT like '%'|| #{actionContent} || '%'

order by ACTION_TIME desc, ACTION_USER asc, ACTION_TYPE asc, ACTION_URL asc
