mapper.select:
select PK_ID, MD_ID, SEQ, NAME, DESCRIBE, TYPE, LENGTH, NOTE, INDEXED, PRIMARY, STORED
from IM_METADATA_COLUMN
where PK_ID = #{pkId,jdbcType=VARCHAR}

select PK_ID, MD_ID, SEQ, NAME, DESCRIBE, TYPE, LENGTH, NOTE, INDEXED, PRIMARY, STORED
from IM_METADATA_COLUMN

  and PK_ID = #{pkId}
  and MD_ID = #{mdId}
  and NAME like '%'||#{name}||'%'
  and DESCRIBE like '%'||#{describe}||'%'
  and NOTE like '%'||#{note}||'%'
  and TYPE = #{type}
  and LENGTH = #{length}
  and SEQ = #{seq}
  and INDEXED = #{indexed}
  and PRIMARY = #{primary}
  and STORED = #{stored}

order by SEQ asc

SELECT IMC.PK_ID, IMC.MD_ID, IMC.SEQ, IMC.NAME, IMC.DESCRIBE, IMC.TYPE, IMC.LENGTH, IMC.NOTE, IMC.INDEXED, IMC.PRIMARY, IMC.STORED
FROM IM_METADATA_COLUMN IMC
WHERE IMC.NAME = #{name}
AND IMC.MD_ID = #{mdId}
