mapper.select:
select PK_ID, APP_ID, SEQ, NAME, DESCRIBE, TYPE, LENGTH, IS_NEED, DEFAULT_VAL, OPERATOR,
LABEL, IS_OFFER_OUT,NOTE
from IQ_APPLICATION_QUERY_COLUMN

  and PK_ID = #{pkId}
  and APP_ID = #{appId}
  and SEQ = #{seq}
  and NAME like '%'||#{name}||'%'
  and DESCRIBE like '%'||#{describe}||'%'
  and TYPE = #{type}
  and LENGTH = #{length}
  and IS_NEED = #{isNeed}
  and DEFAULT_VAL = #{defaultVal}
  and OPERATOR = #{operator}
  and LABEL like '%'||#{label}||'%'
  and IS_OFFER_OUT = #{isOfferOut}

order by SEQ asc
