mapper.select:
select PK_ID, MD_ID, SEQ, NAME, DESCRIBE, TYPE, LENGTH, NOTE, COL_TYPE
from IQ_METADATA_COLUMN
where PK_ID = #{pkId,jdbcType=VARCHAR}

select PK_ID, MD_ID, SEQ, NAME, DESCRIBE, TYPE, LENGTH, NOTE, COL_TYPE
from IQ_METADATA_COLUMN

    and PK_ID = #{pkId}
    and MD_ID = #{mdId}
    and NAME like '%'||#{name}||'%'
    and DESCRIBE like '%'||#{describe}||'%'
    and NOTE like '%'||#{note}||'%'
    and TYPE = #{type}
    and LENGTH = #{length}
    and SEQ = #{seq}
    and COL_TYPE = #{colType}

order by SEQ asc

select IQ.PK_ID from IQ_APPLICATION IQ
where  IQ.MD_ID = #{mdId,jdbcType=VARCHAR}
and DEL_FLG = '0'
