mapper.update:
update MM_MODEL_INFO
set DEL_FLG = '1'
where PK_ID = #{pkId,jdbcType=VARCHAR}

mapper.select:
select mmi.PK_ID, mmi.NAME, mmi.DESCRIBE, mmi.VER_NOTE, mmi.VER_NUM, mmi.STATUS,
mmi.NOTE, mmi.DEL_FLG, mmi.CRT_USER,
mmi.CRT_TIME, mmi.UPT_USER, mmi.UPT_TIME,mc.cn_name AS CONTRACTOR,mmi.MODEL_TYPE
from udsp.MM_MODEL_INFO mmi
left join udsp.mm_contractor mc
on mmi.contractor = mc.pk_id
where mmi.DEL_FLG = '0'
and mmi.NAME like '%'||#{name}||'%'
and mmi.DESCRIBE like '%'||#{describe}||'%'
and mmi.VER_NOTE =#{verNote}
and mmi.STATUS = #{status}
and mmi.NOTE = #{note}
and mmi.CRT_TIME >= #{crtTimeStart}
and #{crtTimeEnd} >= mmi.CRT_TIME
and mmi.UPT_TIME >= #{uptTimeStart}
and #{uptTimeEnd} >= mmi.UPT_TIME
and mmi.CRT_USER like '%'||#{crtUser}||'%'
and mmi.UPT_USER like '%'||#{uptUser}||'%'
and mc.pk_id = #{contractor}
order by mmi.UPT_TIME desc, mmi.CRT_TIME desc, NAME asc
