mapper.insert:
insert into MM_MODEL_PARAM (PK_ID, MM_ID, SEQ, 
  NAME, DESCRIBE, TYPE, 
  LENGTH, NOTE, COL_TYPE
  )
values (#{pkId,jdbcType=VARCHAR}, #{mmId,jdbcType=VARCHAR}, #{seq,jdbcType=DECIMAL}, 
  #{name,jdbcType=VARCHAR}, #{describe,jdbcType=VARCHAR}, #{type,jdbcType=CHAR}, 
  #{length,jdbcType=VARCHAR}, #{note,jdbcType=VARCHAR}, #{colType,jdbcType=VARCHAR}
  )

mapper.select:
select PK_ID, MM_ID, SEQ, NAME, DESCRIBE, TYPE, LENGTH, NOTE, COL_TYPE
from MM_MODEL_PARAM
where PK_ID = #{pkId,jdbcType=VARCHAR}

select PK_ID, MM_ID, SEQ, NAME, DESCRIBE, TYPE, LENGTH, NOTE, COL_TYPE
from MM_MODEL_PARAM

  and PK_ID = #{pkId}
  and MM_ID = #{mmId}
  and NAME like '%'||#{name}||'%'
  and DESCRIBE like '%'||#{describe}||'%'
  and NOTE like '%'||#{note}||'%'
  and TYPE = #{type}
  and LENGTH = #{length}
  and SEQ = #{seq}
  and COL_TYPE = #{colType}

order by SEQ asc
