mapper.insert:
insert into RC_SERVICE (PK_ID, NAME, DESCRIBE, 
  TYPE, APP_ID, DEL_FLG, CRT_USER, 
  CRT_TIME, UPT_USER, UPT_TIME,STATUS
  )
values (#{pkId,jdbcType=VARCHAR}, #{name,jdbcType=VARCHAR}, #{describe,jdbcType=VARCHAR}, 
  #{type,jdbcType=VARCHAR}, #{appId,jdbcType=VARCHAR}, #{delFlg,jdbcType=CHAR}, #{crtUser,jdbcType=VARCHAR}, 
  #{crtTime,jdbcType=VARCHAR}, #{uptUser,jdbcType=VARCHAR}, #{uptTime,jdbcType=VARCHAR}, #{status,jdbcType=CHAR}
  )

mapper.update:
update RC_SERVICE
set DEL_FLG = '1'
where PK_ID = #{pkId,jdbcType=VARCHAR}

mapper.select:
select PK_ID, NAME, DESCRIBE, TYPE, APP_ID, DEL_FLG, CRT_USER, CRT_TIME, UPT_USER,
UPT_TIME,STATUS
from RC_SERVICE
where DEL_FLG = '0'
and NAME like '%'||#{name}||'%'
and DESCRIBE like '%'||#{describe}||'%'
and APP_ID =#{appId}
and TYPE = #{type}
and CRT_TIME >= #{crtTimeStart}
and #{crtTimeEnd} >= CRT_TIME
and UPT_TIME >= #{uptTimeStart}
and #{uptTimeEnd} >= UPT_TIME
and CRT_USER like '%'||#{crtUser}||'%'
and UPT_USER like '%'||#{uptUser}||'%'
and STATUS =#{status}
order by UPT_TIME desc, CRT_TIME desc, NAME asc

select PK_ID, NAME, DESCRIBE, TYPE, APP_ID, DEL_FLG, CRT_USER, CRT_TIME, UPT_USER, UPT_TIME,STATUS
from RC_SERVICE
where DEL_FLG = '0'
and TYPE = #{type,jdbcType=VARCHAR}
and APP_ID = #{appId,jdbcType=VARCHAR}

select PK_ID, NAME, DESCRIBE, TYPE, APP_ID, DEL_FLG, CRT_USER, CRT_TIME, UPT_USER, UPT_TIME, STATUS
from RC_SERVICE
where DEL_FLG = '0' and STATUS = '0'
and TYPE = #{type,jdbcType=VARCHAR}
and APP_ID = #{appId,jdbcType=VARCHAR}

mapper.update:
update RC_SERVICE
set STATUS = #{status,jdbcType=CHAR}
where PK_ID = #{pkId,jdbcType=VARCHAR}
